        public void XuLyBanDau()
        {
            groupDoiTuong.Enabled = false;
            groupBaoCao.Enabled = false;
            groupTaiKhoan.Enabled = false;
        }

        public void XuLyThoat()
        {
            groupDoiTuong.Enabled = false;
            groupBaoCao.Enabled = false;
            groupTaiKhoan.Enabled = false;
            groupHeThong.Enabled = true;
            Ribbon.SelectedPage = pageHeThong;
            lblMa.Text = "Mã: ";
            lblHoTen.Text = "Họ tên: ";
            lblNhom.Text = "Nhóm: ";
        }

        public void XuLyDangNhap()
        {
            // Tự động chuyển tới page hoạt động
            Ribbon.SelectedPage = pageDoiTuong;
            groupDoiTuong.Enabled = true;
            groupBaoCao.Enabled = true;
            groupTaiKhoan.Enabled = true;
            groupHeThong.Enabled = false;
        }

        public void XuLyHienThi()
        {
            if (Program.nhom.Equals("PKETOAN"))
            {
                this.groupDoiTuong.ItemLinks.Clear();
                this.groupDoiTuong.ItemLinks.Add(this.btnHocPhi);
                this.groupDoiTuong.ItemLinks.Add(this.btnDangXuat);

                this.groupBaoCao.ItemLinks.Clear();
                this.groupBaoCao.ItemLinks.Add(this.btnDanhSachDongHocPhi);

                this.groupTaiKhoan.ItemLinks.Clear();
                this.groupTaiKhoan.ItemLinks.Add(this.btnTaoTaiKhoan);
            }
            else
            {
                this.groupDoiTuong.ItemLinks.Clear();
                this.groupDoiTuong.ItemLinks.Add(this.btnLop);
                this.groupDoiTuong.ItemLinks.Add(this.btnSinhVien);
                this.groupDoiTuong.ItemLinks.Add(this.btnMonHoc);
                this.groupDoiTuong.ItemLinks.Add(this.btnDiem);
                this.groupDoiTuong.ItemLinks.Add(this.btnDangXuat);

                this.groupBaoCao.ItemLinks.Clear();
                this.groupBaoCao.ItemLinks.Add(btnDanhSachSinhVien);
                this.groupBaoCao.ItemLinks.Add(btnDanhSachThiHetMon);
                this.groupBaoCao.ItemLinks.Add(btnBangDiemMonHoc);
                this.groupBaoCao.ItemLinks.Add(btnPhieuDiemSinhVien);
                this.groupBaoCao.ItemLinks.Add(btnBangDiemTongKet);

                this.groupTaiKhoan.ItemLinks.Clear();
                this.groupTaiKhoan.ItemLinks.Add(this.btnTaoTaiKhoan);

                if (Program.nhom.Equals("KHOA"))
                {
                    btnLop.Enabled = false;
                    btnMonHoc.Enabled = false;

                    btnTaoTaiKhoan.Enabled = false;
                }
                else if (Program.nhom.Equals("USER"))
                {
                    btnLop.Enabled = false;
                    btnSinhVien.Enabled = false;
                    btnMonHoc.Enabled = false;

                    btnDanhSachSinhVien.Enabled = false;
                    btnDanhSachThiHetMon.Enabled = false;
                    btnBangDiemMonHoc.Enabled = false;
                    btnPhieuDiemSinhVien.Enabled = false;
                    btnBangDiemTongKet.Enabled = false;

                    btnTaoTaiKhoan.Enabled = false;
                }
                
            }
        }

