        }

        public void KhoiPhucTrangHienTai(bool loai)
        {
            if (loai)
            {
                if (Program.nhom.Equals("PGV"))
                {
                    btnLop.Enabled = true;
                    btnSinhVien.Enabled = true;
                    btnMonHoc.Enabled = true;
                    btnDiem.Enabled = true;
                    btnDangXuat.Enabled = true;

                    groupDoiTuong.Enabled = true;
                    groupBaoCao.Enabled = true;
                    groupTaiKhoan.Enabled = true;
                }
                else if (Program.nhom.Equals("KHOA"))
                {
                    btnSinhVien.Enabled = true;
                    btnDiem.Enabled = true;
                    btnDangXuat.Enabled = true;

                    groupDoiTuong.Enabled = true;
                    groupBaoCao.Enabled = true;
                }
                else if (Program.nhom.Equals("USER"))
                {
                    btnDiem.Enabled = true;
                    btnDangXuat.Enabled = true;

                    groupDoiTuong.Enabled = true;
                    groupBaoCao.Enabled = true;
                }
            }
            else
            {
                btnHocPhi.Enabled = true;
                btnDangXuat.Enabled = true;

                groupDoiTuong.Enabled = true;
                groupBaoCao.Enabled = true;
                groupTaiKhoan.Enabled = true;
            }
        }

        public void KhoiPhucTrangBaoCaoHienTai()
        {
            btnDanhSachSinhVien.Enabled = true;
            btnDanhSachThiHetMon.Enabled = true;
            btnBangDiemMonHoc.Enabled = true;
            btnBangDiemTongKet.Enabled = true;
            btnPhieuDiemSinhVien.Enabled = true;

            groupDoiTuong.Enabled = true;
            if (Program.nhom.Equals("PGV") || Program.nhom.Equals("PKETOAN"))
            {
                groupTaiKhoan.Enabled = true;
            }
